function create(e,t,s){$.ajax({url:t,type:"POST",data:e,success:function(e){e.msg&&(swal({text:e.msg,icon:"success"}),$("#"+s).modal("hide"),clearModal())}})}function edit(e,t,s){$.ajax({url:t,type:"GET",success:function(e){setModal(e),$("#"+s).modal("show")}})}function update(e,t,s){$.ajax({url:t,type:"POST",data:e,success:function(e){e.msg&&(swal({text:e.msg,icon:"success"}),$("#"+s).modal("hide"),clearModal())}})}function deleteRes(e,t){swal({title:"Are you sure?",text:"Sure you want to delete this?",icon:"warning",buttons:!0,dangerMode:!0}).then(e=>{e&&$.ajax({url:t,type:"DELETE",headers:{"X-CSRF-TOKEN":$('meta[name="csrf-token"]').attr("content")},success:function(e){e.msg&&swal({text:e.msg,icon:"success"})}})})}function clearModal(){$(".input-cl").val("")}function setModal(e){for(var t in e)$("#"+t).val(e[t])}
